<div class="ewm_r_dark_background_review">

	<div id="ewm_r_pop_address_fields">

		<div class="ewm_r_top_nav_fields">

			<span class="ewm_r_close_box_review"> Close[x] </span>

		</div>

		<div class="ewm_r_main_field" >
		
			<div class="ewm_r_worker_review_title"> <center> Add Worker Review </center> </div> </br>

			<form id="ewm_r_work_review_form">

				<div class="ewm_r_work_review_worker">

					<input name	= "ewm_r_worker_name"		id = "ewm_r_worker_name"		placeholder = "Workers name" required > 
					<br/> <span id = "ewm_r_worker_name_message"> </span>

					<input name	= "ewm_r_job_description"   id = "ewm_r_job_description"	placeholder = "Job Description" required >
					<br/> <span id = "ewm_r_job_description_message"> </span>

				</div>

				<div class="ewm_r_work_review_address">

					<input name = "ewm_r_city" 				id = "ewm_r_city" 				placeholder = "City" required >
					<br/> <span id = "ewm_r_city_message"> </span>

					<input name = "ewm_r_state" 			id = "ewm_r_state" 				placeholder = "State" required >
					<br/> <span id = "ewm_r_state_message"> </span>

					<input name = "ewm_r_address"			id = "ewm_r_address" 			placeholder = "Address" required >
					<br/> <span id = "ewm_r_address_message"> </span>

				</div>

				<div class="ewm_r_worker_review_customer">

					<input name = "ewm_r_review_place" 		id = "ewm_r_review_place" 		placeholder = "Place being Reviewed" >
					<br/> <span id = "ewm_r_review_place_message"> </span>

					<input name = "ewm_r_customer_name" 	id = "ewm_r_customer_name" 		placeholder = "Customer Name" required >
					<br/> <span id = "ewm_r_customer_name_message"> </span>

				</div>

				<div class="ewm_r_worker_review_main">

					<input name = "ewm_r_review_title" 		id = "ewm_r_review_title" 		placeholder = "Review Title" required >
					<br/> <span id = "ewm_r_review_title_message"> </span>

					<input name = "ewm_r_description" 		id = "ewm_r_description"  		placeholder = "Review Description" required >
					<br/> <span id = "ewm_r_description_message"> </span>

					<div id="first">
						<label for="slider1">Review Rating</label><br>
						<input type="range" id="ewm_r_star_rating" name="ewm_r_star_rating" min=0 max=10 value=0>
						<div class="score" id="ratingOne">0</div>
					</div>

					<br/> <span id = "ewm_r_star_rating_message"> </span>

				</div>

				<div class="ewm_r_worker_review_page">

					<input name = "ewm_r_related_page_id"   id = "ewm_r_related_page_id" 	placeholder = "Related Page" required >
					<br/> <span id = "ewm_r_related_page_id_message"> </span>

					<input name = "ewm_r_post_id"   id = "ewm_r_post_id" 	type = "hidden" value = 0 >
					
				</div>

				
			</form>

		</div>


		<div class="ewm_r_work_review_bottom_nav">

			<div id="ewm_r_work_review_pre" > << Previous </div>
			<div id="ewn_r_work_review_next" > Next >> </div>

		</div>

	</div>

</div>
